  //   { method: "PUT", body: JSON.stringify(cart) }
  // );

  // if (!response.ok) {
  //   throw new Error("Sending Cart Data Fail");
  // }
  //   dispatch(
  //     uiActions.showNotification({
  //       status: "success",
