  // }

  // sendCartData().catch((error) => {
  //   dispatch(
  //     uiActions.showNotification({
  //       status: "error",
  //       title: "Error!",
  //       message: "Sending cart data Failed!!",
  //     })
  //   );
  // });
  // }, [cart, dispatch]);

  useEffect(() => {
    if (isInitial) {
      isInitial = false;
      return;
    }

    dispatch(sendCartData(cart));
  }, [cart, dispatch]);

  return (
    <Fragment>
      {notification && (
        <Notification
          status={notification.status}
          title={notification.title}
          message={notification.message}
        />
      )}
      <Layout>
        {showCart && <Cart />}
        <Products />
      </Layout>
    </Fragment>
  );
}

export default App;
